package Loops;

public class WhileLoop {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		//until the loop condition gets false the loop will be executing
		while(i<=10) {
			System.out.println(i);
			i++;
			
		}
	   
	}

}


